import React, { useState } from "react";

const InputBox = ({ currentUser }) => {
  const [userInput, setUserInput] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <form
      onSubmit={(e) => {
        handleSubmit(e);
      }}
      className="input"
    >
      <input
        value={userInput}
        placeholder="Add a comment..."
        type="text"
        onChange={(e) => {
          setUserInput(e.target.value);
        }}
      />
    </form>
  );
};

export default InputBox;
