import React, { useContext, useState } from "react";
import ReplyBtn from "../images/icon-reply.svg";
import Plus from "../images/icon-plus.svg";
import Minus from "../images/icon-minus.svg";
import Delete from "../images/icon-delete.svg";
import Edit from "../images/icon-edit.svg";
import DataContext from "../Context/DataContent";

const Reply = ({ reply }) => {
  const { currentUser } = useContext(DataContext);

  const { content, createdAt, score, replyingTo, user } = reply;
  const [currentScore, setCurrentScore] = useState({
    current: score,
    hasAdded: false,
    hasSubtracted: false,
  });
  const isCurrentUser = user.username === currentUser.username;

  const addToScore = () => {
    const { current, hasAdded } = currentScore;

    if (current !== score && !hasAdded) {
      setCurrentScore({
        hasSubtracted: false,
        current: score,
        hasAdded: false,
      });
    } else if (!hasAdded) {
      setCurrentScore({
        hasSubtracted: false,
        current: score + 1,
        hasAdded: true,
      });
    }
  };

  const subtractScore = () => {
    const { current, hasSubtracted } = currentScore;

    if (current !== score && !hasSubtracted) {
      setCurrentScore({
        hasSubtracted: false,
        current: score,
        hasAdded: false,
      });
    } else if (!hasSubtracted) {
      setCurrentScore({
        hasSubtracted: true,
        current: score - 1,
        hasAdded: false,
      });
    }
  };

  return (
    <div className="comment reply">
      <div className="comment__header">
        <img className="avatar" src={user.image.png} alt="user avatar" />
        <p className="user">{user.username}</p>
        <p className="time">{createdAt}</p>
      </div>
      <p className="comment__content">
        {" "}
        <span className="replyingTo">@{replyingTo}</span> {content}
      </p>
      <div className="comment__footer">
        <div className="comment__likes">
          <button onClick={addToScore} className="plusBtn">
            <img src={Plus} alt="plus" />
          </button>
          <p className="score">{currentScore.current}</p>
          <button onClick={subtractScore} className="minusBtn">
            <img src={Minus} alt="minus" />
          </button>
        </div>
        {!isCurrentUser ? (
          <button className="comment__reply">
            <img className="replyImg" src={ReplyBtn} alt="reply button" /> Reply
          </button>
        ) : (
          <div className="comment__userBtns">
            <button className="comment__delete">
              <img src={Delete} alt="delete" />
              Delete
            </button>
            <button className="comment__edit">
              <img src={Edit} alt="delete" />
              Edit
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reply;
