import React, { useContext, useState } from "react";
import ReplyBtn from "../images/icon-reply.svg";
import Reply from "./Reply";
import Plus from "../images/icon-plus.svg";
import Minus from "../images/icon-minus.svg";
import Delete from "../images/icon-delete.svg";
import Edit from "../images/icon-edit.svg";
import DataContext from "../Context/DataContent";

const Comment = ({ comment }) => {
  const { currentUser } = useContext(DataContext);
  const { content, createdAt, score, user, replies } = comment;
  const [currentScore, setCurrentScore] = useState({
    score,
    hasAdded: false,
    hasSubtracted: false,
  });

  const isCurrentUser = currentUser.username === user.username;

  const addToScore = () => {
    const { hasAdded } = currentScore;

    if (currentScore.score !== score) {
      setCurrentScore({
        hasSubtracted: false,
        score: score,
        hasAdded: false,
      });
    } else if (hasAdded === false) {
      setCurrentScore({
        hasSubtracted: false,
        score: score + 1,
        hasAdded: true,
      });
    }

    console.log(currentScore);
  };

  const subtractScore = () => {
    setCurrentScore({
      hasSubtracted: true,
      score: score - 1,
      hasAdded: false,
    });
  };

  return (
    <>
      <article className="comment">
        <div className="comment__header">
          <img className="avatar" src={user.image.webp} alt="user avatar" />
          <p className="user">{user.username}</p>
          <p className="time">{createdAt}</p>
        </div>
        <p className="comment__content">{content}</p>
        <div className="comment__footer">
          <div className="comment__likes">
            <button onClick={addToScore} className="plusBtn">
              <img src={Plus} alt="plus" />
            </button>
            <p className="score">{currentScore.score}</p>
            <button onClick={subtractScore} className="minusBtn">
              <img src={Minus} alt="minus" />
            </button>
          </div>
          {!isCurrentUser ? (
            <button className="comment__reply">
              <img className="replyImg" src={ReplyBtn} alt="reply button" />{" "}
              Reply
            </button>
          ) : (
            <div className="comment__userBtns">
              <button className="comment__delete">
                <img src={Delete} alt="delete" />
                Delete
              </button>
              <button className="comment__edit">
                <img src={Edit} alt="delete" />
                Edit
              </button>
            </div>
          )}
        </div>
      </article>
      {replies.length > 1 && (
        <article className="replies">
          <div className="replies__offset"></div>
          <div>
            {replies.map((reply, id) => {
              return <Reply key={id} reply={reply} />;
            })}
          </div>
        </article>
      )}
    </>
  );
};

export default Comment;
