import React from "react";
import ReplyBtn from "../images/icon-reply.svg";

const Comment = ({ comment }) => {
  const { content, createdAt, score, user, replies } = comment;

  return (
    <div className="comment">
      <div>
        <img src="" alt="" />
        <p className="comment__user">{user.username}</p>
        <p className="comment__time">{createdAt}</p>
      </div>
      <div className="comment__content">
        <p className="text">{content}</p>
      </div>
      <div className="comment__footer">
        <div className="comment__likes"></div>
        <div>
          <img src={ReplyBtn} alt="reply button" />
          <button className="comment__reply">Reply</button>
        </div>
      </div>
    </div>
  );
};

export default Comment;
